export class IrhBusiness {

    private context: WebPartContext;
    private svcCore: CoreService;
    private ApiUrl: string;

    constructor(props, token?: string) {
        this.context = props.context;
        this.svcCore = new CoreService(props.context);
        this.ApiUrl = props.apiUrl;
        
        headers = { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
        };
    }

    public getBanners = async ():Promise<IBanner[]> => {
        return new Promise((resolve, reject) => {

            //resolve(bannerMockResponse.data);
            axios.get(`${this.ApiUrl}/banners`, {headers})
            .then((response:any) => {
                resolve(response.data.data);
            })
            .catch((error:any) => {
                reject(error);
            })
        });
    }

    public getShelf = async ():Promise<IPrateleira[]> => {
        return new Promise((resolve, reject) => {
            //resolve(prateleirasMockResponse.data);

            axios.get(`${this.ApiUrl}/prateleiras`, {headers})
            .then((response:any) => {
                resolve(response.data.data);
            })
            .catch((error:any) => {
                reject(error);
            })
        });
    }

    public getPillar = async ():Promise<IPilar[]> => {
        return new Promise((resolve, reject) => {

            //resolve(pilaresMockResponse.data);

            axios.get(`${this.ApiUrl}/pilares`, {headers})
            .then((response:any) => {
                resolve(response.data.data);
            })
            .catch((error:any) => {
                reject(error);
            })
        });
    }

    public addFavorite = async (id:number) => {
        return new Promise((resolve, reject) => {
            axios.post(`${this.ApiUrl}/favoritos/${id}`, null, {headers})
            .then((response:any) => {
                resolve(true);
            })
            .catch((error:any) => {
                reject(error);
            })
        });
    }

    public removeFavorite = async (id:number) => {
        return new Promise((resolve, reject) => {
            axios.delete(`${this.ApiUrl}/favoritos/${id}`, {headers})
            .then((response:any) => {
                resolve(true);
            })
            .catch((error:any) => {
                reject(error);
            })
        });
    }

    public getCurrentUserUserProfile = async () => {
        return this.svcCore.currentUserSP();
    }
}